/* For testing purposes, we'll include a minimal implementation */

(function() {
    // Modal implementation
    document.addEventListener('DOMContentLoaded', function() {
        // Modal show/hide
        document.querySelectorAll('[data-bs-toggle="modal"]').forEach(function(element) {
            element.addEventListener('click', function() {
                const target = document.querySelector(element.getAttribute('data-bs-target'));
                if (target) {
                    target.style.display = 'block';
                    document.body.classList.add('modal-open');
                }
            });
        });
        
        // Modal close buttons
        document.querySelectorAll('[data-bs-dismiss="modal"]').forEach(function(element) {
            element.addEventListener('click', function() {
                const modal = element.closest('.modal');
                if (modal) {
                    modal.style.display = 'none';
                    document.body.classList.remove('modal-open');
                }
            });
        });
        
        // Dropdown implementation
        document.querySelectorAll('.dropdown-toggle').forEach(function(element) {
            element.addEventListener('click', function(e) {
                e.preventDefault();
                const parent = element.parentElement;
                const menu = parent.querySelector('.dropdown-menu');
                if (menu) {
                    menu.style.display = menu.style.display === 'block' ? 'none' : 'block';
                }
            });
        });
        
        // Close dropdowns when clicking outside
        document.addEventListener('click', function(e) {
            if (!e.target.matches('.dropdown-toggle')) {
                document.querySelectorAll('.dropdown-menu').forEach(function(menu) {
                    menu.style.display = 'none';
                });
            }
        });
        
        // Navbar toggler
        document.querySelectorAll('.navbar-toggler').forEach(function(element) {
            element.addEventListener('click', function() {
                const target = document.querySelector(element.getAttribute('data-bs-target'));
                if (target) {
                    target.classList.toggle('show');
                }
            });
        });
    });
})();
