def fibonacci(n):
    fib_sequence = [0, 1]
    if n <= 0:
        return []
    if n == 1:
        return [0]
    if n == 2:
        return fib_sequence

    for i in range(2, n):
        fib_sequence.append(fib_sequence[i-1] + fib_sequence[i-2])

    return fib_sequence

function isPalindrome(str) {
  const cleanStr = str.toLowerCase().replace(/[^a-z0-9]/g, '');
  return cleanStr === cleanStr.split('').reverse().join('');
